import React, { Component } from "react";
import Puppy from "./components/Puppy";
import puppies from "./cards.json";
import "./App.css";

class App extends Component {
  state = {
    puppies,
    clickedPuppies: [],
    score: 0,
    topScore: 0,
  };

  puppyClick = id => {
    if (this.state.clickedPuppies.indexOf(id) === -1) {
      this.scoreIncrement();
      this.state.clickedPuppies.push(id);
    }
    else {
      this.reset();
    }
  }

  scoreIncrement = () => {
      const newScore = this.state.score + 1
      this.setState({score: newScore});
      if (newScore > this.state.topScore){
        this.setState({topScore: newScore})
      }

      this.shuffle();
  }

  reset = () => {
    this.setState({
      score: 0,
      topScore: this.state.topScore,
      clicked: []
    });

    this.shuffle();
  }

  shuffle = (puppies) => {
    for (let i= 0; i<puppies.length-1; i++){
      let j = Math.floor(Math.random()*(i+1));
      [puppies[i], puppies[j]] = [puppies[j], puppies[i]]
    }
    return puppies;
  }

  render() {
    return (
      <div>
        <nav class="navbar navbar-light bg-light">
          <span class="navbar-brand mb-0 h1">Clicky Game!</span>
          <ul class="nav">
            <li class="nav-item">
              <span>Click an Image to Begin</span>
            </li>
            <li class="nav-item">
              Score: 
              0
               | Top Score: 
              0
            </li>
          </ul>
        </nav>
        <div class="container">
          {this.state.puppies.map(puppy => (
            <Puppy
              id={this.id}
              key={this.id}
              image={this.image}
              puppyClick = {this.puppyClick}
              scoreIncrement = {this.scoreIncrement}
              reset = {this.reset}
              shuffle = {this.shuffle}
            />
          ))}
        </div>
      </div>
    );
  }
}

export default App;
